import "react-dropdown/style.css";
import styled from "styled-components";
function App() {
  const [selectedOption, setSeletedOption] = useState(null);
  const options = ["one", "two", "three"];
  const defaultOption = options[0];
  <Dropdown
    options={options}
    onChange={handl}
    value={defaultOption}
    placeholder="Select an option"
  />;
  return (
    <Offer className="offer">
      <OfferText>
        <p>
          Summer Sale For All Swim Suits And Free Express Delivery - OFF 50%!
        </p>
        <Link>Shop Now</Link>
      </OfferText>
      <Dropdown />
    </Offer>
  );
}

export default App;

const Offer = styled.div`
  background: #000;
  color: #fff;
  font-size: 14px;
  display: flex;
  flex-direction: row;
  justify-content: center;
  align-items: center;
  padding: 6px 0;
`;

const Link = styled.a`
  color: #fff;
  font-weight: 700;
  text-decoration: underline;
  margin-left: 6px;
`;

const OfferText = styled.div`
  display: flex;
  flex-direction: row;
  justify-content: center;
  align-items: center;
`;

const Language = styled.div`
  display: flex;
  flex-direction: row;
  justify-content: center;
  align-items: center;
  margin-left: 6px;
`;
