import styled from "styled-components";

import Star from "../../assets/Star.svg";
import StarUnfilled from "../../assets/Star-unfilled.svg";
import QuickView from "../../assets/Quick View.svg";
import WishList from "../../assets/wishlist.svg";
const CategoryContent = [
  {
    id: 1,
    name: "Breed Dry Dog Food",
    image: require("../../assets/Dogfood.png"),
    price: "$100",
    rating: "4.9",
    category: "Dogs",
  },
  {
    id: 2,
    name: "CANON EOS DSLR Camera",
    image: require("../../assets/Camera.png"),
    price: "$100",
    rating: "4.9",
    category: "Dogs",
  },
  {
    id: 3,
    name: "ASUS FHD Gaming Laptop",
    image: require("../../assets/Laptop.png"),
    price: "$100",
    rating: "4.9",
    category: "Dogs",
  },
  {
    id: 4,
    name: "Curology Product Set ",
    image: require("../../assets/Cosmetics.png"),
    price: "$100",
    rating: "4.9",
    category: "Dogs",
  },
  {
    id: 5,
    name: "Kids Electric Car",
    image: require("../../assets/Car.png"),
    price: "$100",
    rating: "4.9",
    category: "Dogs",
  },
  {
    id: 6,
    name: "Jr. Zoom Soccer Cleats",
    image: require("../../assets/Shoe.png"),
    price: "$100",
    rating: "4.9",
    category: "Dogs",
  },
  {
    id: 7,
    name: "GP11 Shooter USB Gamepad",
    image: require("../../assets/Game.png"),
    price: "$100",
    rating: "4.9",
    category: "Dogs",
  },
  {
    id: 8,
    name: "Quilted Satin Jacket",
    image: require("../../assets/Jacket.png"),
    price: "$100",
    rating: "4.9",
    category: "Dogs",
  },
];
export default function ProductPage() {
  return (
    <>
      <ProductConatiner>
        <ProductList>
          {CategoryContent.map((category) => (
            <Product>
              <ProductImage>
                <img src={category.image} alt="Image" />
                <IconList>
                  <img src={WishList} alt="Wish List" />
                  <img src={QuickView} alt="Quick View" />
                </IconList>
                <button>Add To Cart</button>
              </ProductImage>
              <ProductInfo>
                <ProductName>{category.name}</ProductName>
                <ProductDetails>
                  <ProductPrice>{category.price}</ProductPrice>
                  <ProductRating>
                    <StarListing>
                      <RatingStar src={Star} alt="Image" />
                      <RatingStar src={Star} alt="Image" />
                      <RatingStar src={Star} alt="Image" />
                      <RatingStar src={Star} alt="Image" />
                      <RatingStar src={Star} alt="Image" />
                      <RatingStar src={StarUnfilled} alt="Image" />
                    </StarListing>
                    <p>({category.rating})</p>
                  </ProductRating>
                </ProductDetails>
              </ProductInfo>
            </Product>
          ))}
        </ProductList>
        <ViewAllButton>View All Products</ViewAllButton>
      </ProductConatiner>
    </>
  );
}

const ProductConatiner = styled.div`
  display: flex;
  flex-direction: column;
  justify-content: center;
  align-items: center;
  width: 100%;
`;

const ProductList = styled.ul`
  display: flex;
  flex-direction: row;
  flex-wrap: wrap;
  justify-content: space-between;
  align-items: center;
  padding: 0;
  margin-bottom: 40px;
  width: 100%;
  @media screen and (max-width: 620px) {
    margin-bottom: 20px;
  }
  @media screen and (max-width: 620px) {
    justify-content: space-evenly;
  }
`;

const Product = styled.li`
  display: flex;
  flex-direction: column;
  justify-content: center;
  align-items: flex-start;
  max-width: 20%;
  max-height: 326px;
  margin: 0 20px 40px 0;
  cursor: pointer;
  @media screen and (max-width: 1024px) {
    max-height: 260px;
  }
  @media screen and (max-width: 960px) {
    max-height: 240px;
    max-width: 22%;
    margin: 0 18px 36px 0;
  }
  @media screen and (max-width: 820px) {
    max-height: 220px;
    margin: 0 16px 32px 0;
  }
  @media screen and (max-width: 720px) {
    max-height: 200px;
    margin: 0 12px 20px 0;
  }
  @media screen and (max-width: 540px) {
    max-width: 28%;
  }
  @media screen and (max-width: 425px) {
    max-width: 30%;
  }
  @media screen and (max-width: 375px) {
    max-width: 42%;
  }
`;

const ProductImage = styled.div`
  border-radius: 4px;
  background-color: #f0f0f0;

