import React, { useState, useCallback } from "react";
import Dropdown from "react-dropdown";
import "react-dropdown/style.css";
import styled from "styled-components";
import "../../";

const App = () => {
  const [selectedOption, setSelectedOption] = useState(null);

  const handleSelect = useCallback((option) => {
    setSelectedOption(option);
    console.log(option);
  }, []);

  const options = ["one", "two", "three"];
  const defaultOption = options[0];

  return (
    <Offer className="offer">
      <OfferText>
        <p>
          Summer Sale For All Swim Suits And Free Express Delivery - OFF 50%!
        </p>
        <Link>Shop Now</Link>
      </OfferText>
      <StyledDropdown
        options={options}
        onChange={handleSelect}
        value={defaultOption}
        placeholder="Select an option"
      />
    </Offer>
  );
};

export default App;

const Offer = styled.div`
  background: #000;
  color: #fff;
  font-size: 14px;
  display: flex;
  flex-direction: row;
  justify-content: center;
  align-items: center;
  padding: 6px 0;
`;

const Link = styled.a`
  color: #fff;
  font-weight: 700;
  text-decoration: underline;
  margin-left: 6px;
`;

const OfferText = styled.div`
  display: flex;
  flex-direction: row;
  justify-content: center;
  align-items: center;
`;

const Language = styled.div`
  display: flex;
  flex-direction: row;
  justify-content: center;
  align-items: center;
  margin-left: 6px;
`;

const StyledDropdown = styled(Dropdown)`
  width: 200px;
  height: 30px;
  border-radius: 5px;
  padding: 10px;
  font-size: 16px;
  display: flex;
  align-self: flex-end;

  .Dropdown-control {
    border: none;
    font-size: 16px;
    background-color: #000;
    color: #fff;
  }

  .Dropdown-menu {
    background-color: #f9f9f9;
    border: none;
    padding: 10px;
    font-size: 16px;
  }

  .Dropdown-option {
    padding: 10px;
    font-size: 16px;
  }
`;
