import styled from "styled-components";
import CellPhone from "../../assets/CellPhone.svg";
import Camera from "../../assets/Camera.svg";
import Computer from "../../assets/Computer.svg";
import Headphone from "../../assets/Headphone.svg";
import Gamepad from "../../assets/Gamepad.svg";
import SmartWatch from "../../assets/SmartWatch.svg";

const CategoriesListContent = [
  {
    id: 1,
    name: "CellPhone",
    image: CellPhone,
  },
  {
    id: 2,
    name: "Camera",
    image: Camera,
  },
  {
    id: 3,
    name: "Computer",
    image: Computer,
  },
  {
    id: 4,
    name: "Headphone",
    image: Headphone,
  },
  {
    id: 5,
    name: "Gamepad",
    image: Gamepad,
  },
  {
    id: 6,
    name: "SmartWatch",
    image: SmartWatch,
  },
];

export default function Categories() {
  return (
    <CategoriesContainer>
      <Divider>
        <div></div>
        <p>Categories</p>
      </Divider>
      <SectionTitle>Browse By Category</SectionTitle>
      <Category>
        <CategoriesListContainer>
          {CategoriesListContent.map((category) => (
            <CategoriesList key={category.id}>
              <Image src={category.image} alt="Image" />
              <CategoriesName>{category.name}</CategoriesName>
            </CategoriesList>
          ))}
        </CategoriesListContainer>
      </Category>
    </CategoriesContainer>
  );
}

const CategoriesContainer = styled.div`
  display: flex;
  flex-direction: column;
  justify-content: center;
  align-items: flex-start;
  margin-top: 60px;
  margin-bottom: 60px;
  width: 100%;
`;

const Divider = styled.div`
  display: flex;
  flex-direction: row;
  justify-content: center;
  align-items: center;
  color: #db4444;
  div {
    width: 20px;
    height: 32px;
    background-color: #db4444;
    border-radius: 5px;
  }
  p {
    margin-left: 12px;
    font-size: 16px;
  }
`;
const SectionTitle = styled.div`
  display: flex;
  flex-direction: row;
  justify-content: center;
  margin: 20px 0px;
  font-size: 36px;
  font-weight: 600;
`;

const Category = styled.ul`
  display: flex;
  flex-direction: row;
  justify-content: center;
  align-items: flex-start;
  margin: 40px 0px;
  padding: 0;
  width: 100%;
`;

const CategoriesListContainer = styled.li`
  display: flex;
  flex-direction: row;
  justify-content: center;
  align-items: center;
  width: 100%;
  cursor: pointer;
`;
const CategoriesList = styled.a`
  display: flex;
  flex-direction: column;
  justify-content: center;
  align-items: center;
  border: 1px solid #808080;
  width: 100%;
  height: 120px;
  padding: 24px 0px;
  border-radius: 4px;
  margin-right: 28px;
  &:nth-child(6) {
    margin-right: 0;
  }
  &:hover {
    border: 1px solid #db4444;
    background-color: #f0f0f0;
  }
`;

const Image = styled.img`
  width: 50px;
  height: 50px;
`;
const CategoriesName = styled.div`
  font-size: 16px;
  font-weight: 600;
  margin-top: 12px;
  text-align: center;
  color: #000;
  text-decoration: none;
`;
