import styled from "styled-components";

const FooterListContent = [
  {
    id: 1,
    title: "Support",
    content: [
      {
        id: 1,
        text: "111 Bijoy sarani, Dhaka,  DH 1515, Bangladesh.",
      },
      {
        id: 2,
        text: "exclusive@gmail.com",
      },
      {
        id: 3,
        text: "+88015-88888-9999",
      },
    ],
  },
  {
    id: 2,
    title: "Account",
    content: [
      {
        id: 1,
        text: "My Account",
      },
      {
        id: 2,
        text: "Login / Register",
      },
      {
        id: 3,
        text: "Cart",
      },
      {
        id: 4,
        text: "Wishlist",
      },
      {
        id: 5,
        text: "Shop",
      },
    ],
  },
  {
    id: 3,
    title: "Quick Link",
    content: [
      {
        id: 1,
        text: "Privacy Policy.",
      },
      {
        id: 2,
        text: "Terms Of Use",
      },
      {
        id: 3,
        text: "FAQ",
      },
      {
        id: 4,
        text: "Contact",
      },
    ],
  },
];
export default function Footer() {
  return (
    <FooterContainer>
      <FooterListContainer>
        <FooterListFirst>
          <h1>Exclusive</h1>
          <h2>Subscribe</h2>
          <p>Get 10% off your first order</p>
          <input placeholder="Enter your email" type="email" />
        </FooterListFirst>
        {FooterListContent.map((footer) => (
          <FooterList>
            <FooterListTitle>{footer.title}</FooterListTitle>
            {footer.content.map((content) => (
              <FooterListLink href="/">{content.text}</FooterListLink>
            ))}
          </FooterList>
        ))}

        <FooterListLast>
          <h1>Download App</h1>
          <p>Save $3 with App New User Only</p>
        </FooterListLast>
      </FooterListContainer>
    </FooterContainer>
  );
}

const FooterContainer = styled.div`
  display: flex;
  flex-direction: column;
  justify-content: center;
  align-items: flex-start;
  width: 100%;
  border-top: 1px solid #000;
  background-color: #000;
  color: #fff;
  font-size: 12px;
  padding: 20px 0;
`;

const FooterListContainer = styled.div`
  display: flex;
  flex-direction: row;
  justify-content: center;
  align-items: flex-start;
  width: 100%;
  padding: 20px 0;
`;
const FooterListFirst = styled.div`
  display: flex;
  flex-direction: column;
  justify-content: center;
  align-items: flex-start;
  padding: 0;
  margin: 0;
  width: 100%;
  h1 {
    font-size: 24px;
    font-weight: 700;
    margin: 0;
    margin-bottom: 20px;
  }
  h2 {
    font-size: 20px;
    font-weight: 700;
    margin: 0;
    margin-bottom: 16px;
  }
  p {
    font-size: 14px;
    color: #fff;
    margin: 0;
    margin-bottom: 16px;
  }
  input {
    width: fit-content;
    padding: 12px;
    border-radius: 5px;
    border: 1px solid #fff;
    background-color: #000;
    color: #fff;
    margin-bottom: 20px;
    font-size: 14px;
    &:focus {
      outline: none;
    }
  }
`;
const FooterList = styled.ul`
  display: flex;
  flex-direction: column;
  justify-content: center;
  align-items: flex-start;
  padding: 0;
  margin: 0;
  width: 100%;
  list-style: none;
`;

const FooterListTitle = styled.li`
  font-size: 20px;
  font-weight: 500;
  margin-bottom: 20px;
`;

const FooterListLink = styled.a`
  font-size: 14px;
  color: #fff;
  width: 175px;
  text-decoration: none;
  margin-bottom: 12px;
  &:hover {
    text-decoration: underline;
  }
`;

const FooterListText = styled.li`
  font-size: 14px;
  color: #fff;
  text-decoration: none;
  margin-bottom: 10px;
`;

const FooterListLast = styled.div`
  display: flex;
  flex-direction: column;
  justify-content: center;
  align-items: flex-start;
  padding: 0;
  margin: 0;
  width: 100%;
`;
