import Footer from "../includes/Footer";
import Headers from "../includes/Header";
import { CategoryContent } from "../screens/ProductPage";

import Star from "../../assets/Star.svg";
import StarUnfilled from "../../assets/Star-unfilled.svg";
import QuickView from "../../assets/Quick View.svg";
import WishList from "../../assets/wishlist.svg";
import styled from "styled-components";
import { Link, useParams } from "react-router-dom";

export default function Product() {
  const { category_id } = useParams();
  return (
    <>
      <Headers />
      <ProductConatiner>
        <ProductList>
          {CategoryContent.map((category) => (
            <Products>
              <Link
                to={`/product/${category.id}`}
                style={{ width: "100%", textDecoration: "none" }}
              >
                <ProductImage>
                  <img src={category.image} alt="Image" />
                  <IconList>
                    <img src={WishList} alt="Wish List" />
                    <img src={QuickView} alt="Quick View" />
                  </IconList>
                  <button>Add To Cart</button>
                </ProductImage>
                <ProductInfo>
                  <ProductName>{category.name}</ProductName>
                  <ProductDetails>
                    <ProductPrice>{category.price}</ProductPrice>
                    <ProductRating>
                      <StarListing>
                        <RatingStar src={Star} alt="Image" />
                        <RatingStar src={Star} alt="Image" />
                        <RatingStar src={Star} alt="Image" />
                        <RatingStar src={Star} alt="Image" />
                        <RatingStar src={Star} alt="Image" />
                        <RatingStar src={StarUnfilled} alt="Image" />
                      </StarListing>
                      <p>({category.rating})</p>
                    </ProductRating>
                  </ProductDetails>
                  <ColorDetails>
                    {category.colors?.map((color, index) => (
                      <ProductColor
                        key={index}
                        style={{ backgroundColor: `${color.color}` }}
                        onClick={styledProduct}
                      ></ProductColor>
                    ))}
                  </ColorDetails>
                </ProductInfo>
              </Link>
            </Products>
          ))}
        </ProductList>
      </ProductConatiner>
      <Footer />
    </>
  );
}

const ProductConatiner = styled.div`
  display: flex;
  flex-direction: column;
  justify-content: center;
  align-items: center;
  width: 100%;
`;

const ProductList = styled.ul`
  display: flex;
  flex-direction: row;
  flex-wrap: wrap;
  justify-content: space-between;
  align-items: center;
  padding: 0;
  margin-bottom: 20px;
  width: 100%;
  @media screen and (max-width: 620px) {
    justify-content: space-evenly;
  }
`;

const Products = styled.li`
  display: flex;
  flex-direction: column;
  justify-content: center;
  align-items: flex-start;
  max-width: 20%;
  margin: 0 20px 40px 0;
  cursor: pointer;
  text-decoration: none;

  @media screen and (max-width: 1024px) {
    max-height: 260px;
    max-width: 20%;
  }
  @media screen and (max-width: 960px) {
    max-height: 240px;
    max-width: 22%;
    margin: 0 18px 36px 0;
  }
  @media screen and (max-width: 820px) {
    max-height: 220px;
    margin: 0 16px 32px 0;
  }
  @media screen and (max-width: 720px) {
    max-height: 200px;
    margin: 0 12px 20px 0;
  }
  @media screen and (max-width: 540px) {
    max-width: 28%;
  }
  @media screen and (max-width: 425px) {
    max-width: 30%;
  }
  @media screen and (max-width: 375px) {
    max-width: 42%;
  }
`;

const ProductImage = styled.div`
  border-radius: 4px;
  background-color: #f0f0f0;

  display: flex;
  min-height: 260px;
  min-width: 260px;
  flex-direction: column;
  justify-content: center;
  align-items: center;
