  }
  @media screen and (max-width: 820px) {
    min-height: 160px;
    min-width: 160px;
  }
  @media screen and (max-width: 720px) {
    min-height: 140px;
    min-width: 130px;
  }

  @media screen and (max-width: 580px) {
    min-height: 120px;
    min-width: 100%;
  }
  @media screen and (max-width: 540px) {
    min-height: 120px;
    min-width: 100%;
  }

  img {
    width: 60%;
    height: 60%;
    border-radius: 4px;
    object-position: center;
    object-fit: contain;
    @media screen and (max-width: 1024px) {
      width: 55%;
