import styled from "styled-components";

import Footer from "../includes/Footer";
import Headers from "../includes/Header";
import { CategoryContent } from "../screens/ProductPage";

import { Link, useParams } from "react-router-dom";

import Star from "../../assets/Star.svg";
import StarUnfilled from "../../assets/Star-unfilled.svg";
import Return from "../../assets/Icon-return.svg";
import Delivery from "../../assets/icon-delivery.svg";

export default function Product() {
  return (
    <>
      <Headers />
      <ProductConatiner>
        <BreadCrumb>
          <Link
            to="/"
            style={{
              textDecoration: "none",
              marginRight: "10px",
              color: "rgba(0, 0, 0, 0.5)",
            }}
          >
            Home
          </Link>{" "}
          /{" "}
          <p
            style={{ textDecoration: "none", margin: "0", marginLeft: "10px" }}
          ></p>
        </BreadCrumb>
        {CategoryContent.slice(0, 1).map((category) => (
          <Products>
            <ProductImage>
              <img src={category.image} alt="Image" />
            </ProductImage>
            <ProductInfo>
              <ProductName>{category.name}</ProductName>
              <ProductRating>
                <StarListing>
                  <RatingStar src={Star} alt="Image" />
                  <RatingStar src={Star} alt="Image" />
                  <RatingStar src={Star} alt="Image" />
                  <RatingStar src={Star} alt="Image" />
                  <RatingStar src={Star} alt="Image" />
                  <RatingStar src={StarUnfilled} alt="Image" />
                </StarListing>
                <p style={{ margin: "0 10px 0 6px" }}>
                  ({category.rating} reviews)
                </p>{" "}
                {"|"}
                {category.details.inStock == true ? (
                  <p style={{ color: "#3fa60b", marginLeft: "10px" }}>
                    in stock
                  </p>
                ) : (
                  <p style={{ color: "#ff0000", marginLeft: "10px" }}>
                    Out of stock
                  </p>
                )}
              </ProductRating>
              <ProductPrice>{category.price}</ProductPrice>
              {category.details && category.details.description ? (
                <Description>{category.details.description}</Description>
              ) : null}
