import styled from "styled-components";

const WishList = require("../../assets/wishlist.svg");

const AboutList = [
  {
    id: 1,
    image: require("/src/assets/icon-delivery.svg"),
    title: "FREE AND FAST DELIVERY",
    description: "Free delivery for all orders over $140",
  },
  {
    id: 1,
    title: "FREE AND FAST DELIVERY",
    description: "Free delivery for all orders over $140",
  },
  {
    id: 1,
    title: "FREE AND FAST DELIVERY",
    description: "Free delivery for all orders over $140",
  },
];
export default function About() {
  return (
    <AboutContainer>
      <AboutListConatiner>
        <Icon>
          <img src={WishList} alt="Wish List" />
        </Icon>
        <Title></Title>
        <Description></Description>
      </AboutListConatiner>
    </AboutContainer>
  );
}

const AboutContainer = styled.div`
  display: flex;
  flex-direction: column;
  justify-content: center;
  align-items: center;
  margin: 0 auto;
`;

const AboutListConatiner = styled.div`
  display: flex;
  flex-direction: column;
  justify-content: center;
  align-items: center;
  margin-top: 60px;
  margin-bottom: 60px;
  width: 100%;
`;

const Icon = styled.div`
  display: flex;
  flex-direction: column;
  justify-content: center;
  align-items: center;
  width: 100%;
  img {
    width: 50px;
    height: 50px;
  }
`;

const Title = styled.div`
  font-size: 16px;
  font-weight: 600;
  margin-top: 12px;
  text-align: center;
  color: #000;
  text-decoration: none;
`;

const Description = styled.div`
  font-size: 16px;
  font-weight: 600;
  margin-top: 12px;
  text-align: center;
  color: #000;
  text-decoration: none;
`;
