              <ProductInfo>
                <ProductName>{category.name}</ProductName>
                <ProductRating>
                  <StarListing>
                    <RatingStar src={Star} alt="Image" />
                    <RatingStar src={Star} alt="Image" />
                    <RatingStar src={Star} alt="Image" />
                    <RatingStar src={Star} alt="Image" />
                    <RatingStar src={Star} alt="Image" />
                    <RatingStar src={StarUnfilled} alt="Image" />
                  </StarListing>
                  <p>({category.rating})</p>| in stock
                </ProductRating>
                <ProductPrice>{category.price}</ProductPrice>
                {category.details && category.details.description ? (
                  <Description>{category.details.description}</Description>
                ) : null}
                <Divider></Divider>
                <ColorDetails>
                  {category.colors?.map((color, index) => (
                    <ProductColor
                      key={index}
                      style={{ backgroundColor: `${color.color}` }}
                      onClick={styledProduct}
                    ></ProductColor>
                  ))}
                </ColorDetails>
              </ProductInfo>
            </Products>
          ))}
        </ProductList>
      </ProductConatiner>
      <Footer />
    </>
  );
}

